tickets, [13671253,634,"bkpnJ4jEWr37jceJe5afGb",null,"b516sOlY8r3PPQeJe5afGb","Daylight savings time issue with server side based maximum expiry date",4,"When you start an upload between 23:00 and 24:00 on a day in 'wintertime' and the maximum expiry date is on a date after switching to summer time the shown and sent maximum expiry date will be one day off (later than the 'expected' day). ","2012-01-27T10:15:24.000+00:00","2012-03-01T07:30:49.000+00:00",394418,null,"bkpnJ4jEWr37jceJe5afGb,aNSALiyQSr3482eJe5cbCb,b8-41KDuKr344jeJe5cbLA",null,0.0,0,634.0,0,1,3325193,1,0.0,0.0,0.0,0.0,null,null]
tickets, [14386233,635,"dPS9kyuyur3zYrab7jnrAJ",null,"b516sOlY8r3PPQeJe5afGb","Enable theming [L]",2,"A fresh ticket.  This feature was suggested in the early start of the project (ticket #79).\n\nTheming has the same basic constraint as all our improvements: no extra server-side installation should be needed.  \n\nThe desired end result is probably close to the Wordpress experience.  ","2012-02-07T10:50:08.000+00:00","2016-02-26T14:03:09.000+00:00",114623,null,"dPS9kyuyur3zYrab7jnrAJ,aNSALiyQSr3482eJe5cbCb,b8-41KDuKr344jeJe5cbLA","2016-02-26T14:03:09.000+00:00",0.0,0,635.0,0,1,447388,0,0.0,0.0,0.0,0.0,"2016-02-26T14:03:09.000+00:00",null]
