tickets, [58259513,931,"bkpnJ4jEWr37jceJe5afGb","bkpnJ4jEWr37jceJe5afGb","b516sOlY8r3PPQeJe5afGb","Translations for new language items",3,"// New items for My Files\n$lang[\"_DOWNLOADED\"] = \"Downloaded\";\n$lang[\"_SHOW_ALL\"] = \"Show/Hide Details\";\n$lang[\"_DETAILS\"] = \"Details\";\n\n// Terasender Advanced Settings\n$lang[\"_TERA_ADVANCED_SETTINGS\"] = \"Advanced Settings\";\n$lang[\"_TERA_CHUNKSIZE\"] = \"Chunksize (MB)\";\n$lang[\"_TERA_WORKER_COUNT\"] = \"Worker count\";\n$lang[\"_TERA_JOBS_PER_WORKER\"] = \"Jobs per workers\";\n","2013-06-29T10:53:36.000+00:00","2016-02-06T06:47:49.000+00:00",3026383,null,"bkpnJ4jEWr37jceJe5afGb","2016-02-06T06:47:49.000+00:00",0.0,0,-29.0,0,0,19299663,0,1.0,1.0,0.0,0.0,"2016-02-06T06:47:49.000+00:00",null]
