tickets, [58551373,942,"aTWq58rMCr4ysmacwqjQXA",null,"b516sOlY8r3PPQeJe5afGb","Remove HTML5 image",3,"Remove the HTML5 image in the top right of the page. HTML5 support is now common enough across browsers that it does not need to be indicated, except in the case where it's _not_ supported (and the Flash interface is loaded).","2013-07-03T12:40:00.000+00:00","2013-07-08T12:53:14.000+00:00",3839423,null,"aTWq58rMCr4ysmacwqjQXA","2013-07-08T12:53:14.000+00:00",0.0,0,-28.0,0,0,447388,0,1.0,1.0,0.0,0.0,"2013-07-08T12:53:14.000+00:00",null]
tickets, [58551973,943,"aTWq58rMCr4ysmacwqjQXA","aTWq58rMCr4ysmacwqjQXA","b516sOlY8r3PPQeJe5afGb","Update !DOCTYPE declaration",3,"Document type declaration should be changed to <!DOCTYPE HTML> to properly reflect that Filesender uses HTML5. The old \"XHTML Transitional\" doctype causes validation to fail in cases where new HTML5 attributes are used.\n\nWill need to revalidate HTML after changing.","2013-07-03T12:47:53.000+00:00","2013-08-15T13:35:20.000+00:00",3839423,null,"aTWq58rMCr4ysmacwqjQXA",null,0.0,0,-29.0,0,0,238024,1,1.0,1.0,0.0,0.0,"2013-08-15T13:35:20.000+00:00",null]
