tickets, [61906513,977,"aTWq58rMCr4ysmacwqjQXA","aTWq58rMCr4ysmacwqjQXA","b516sOlY8r3PPQeJe5afGb","Add check for 64-bit PHP installation",3,"Credit to Jan for pointing this one out:\n\nA 64-bit version of PHP is required for much of FileSender to work properly, but this is not currently being checked anywhere in the code. An error message (similar to what happens if config.php is not found) should be generated if the PHP installation is found to be 32-bit aware only.\n\nSee http://stackoverflow.com/a/6304354 for one possible way to perform such a check.","2013-08-14T12:52:33.000+00:00","2013-08-15T13:48:18.000+00:00",3839423,null,"aTWq58rMCr4ysmacwqjQXA",null,0.0,0,-56.0,0,0,238024,1,1.0,1.0,0.0,0.0,"2013-08-15T13:48:18.000+00:00",null]
tickets, [61982543,978,"bIiF-SXuOr4ydAacwqjQWU","bIiF-SXuOr4ydAacwqjQWU","b516sOlY8r3PPQeJe5afGb","Guest voucher consumed on failed upload",3,"If for any reason an upload fails (browser crash, server error, etc) the issued guest voucher gets used and is no longer usable, a new one has to be issued.\nThis shouldn't be the case, the voucher should only be consumed when a transaction has successfully finished uploading.","2013-08-15T08:55:12.000+00:00","2013-10-01T16:04:29.000+00:00",3839423,null,"bIiF-SXuOr4ydAacwqjQWU",null,0.0,0,-57.0,0,0,238024,1,1.0,1.0,0.0,0.0,"2013-09-03T10:51:08.000+00:00",null]
