tickets, [95543183,1130,"bkpnJ4jEWr37jceJe5afGb",null,"b516sOlY8r3PPQeJe5afGb","Crypto: re-assess default suggested password",3,"Although cryptographically good the default suggestion for a password is 'too long' for use in for example text message (out of band). People tend to not use the suggested passphrase and might be inclined to use a way too short or easy to bruteforce password.","2014-08-27T13:42:18.000+00:00","2014-08-27T13:42:18.000+00:00",1379273,null,"bkpnJ4jEWr37jceJe5afGb",null,0.0,0,-37.0,0,0,28660,1,1.0,1.0,0.0,0.0,"2014-08-27T13:42:18.000+00:00",null]
tickets, [95544203,1131,"bkpnJ4jEWr37jceJe5afGb","bkpnJ4jEWr37jceJe5afGb","b516sOlY8r3PPQeJe5afGb","Crypto: consolidate HELP text",3,"Currently the HELP text is not adapted for the crypto functionality. Basically just a few extra remarks are locally prepended to the existing default help text on our pilot installation. This should be merged in with the main help text. Note that this should be dependent on the to be implemented configurable to enable/disable the crypto module (see #1112)","2014-08-27T13:50:44.000+00:00","2015-02-17T15:17:36.000+00:00",1379273,null,"bkpnJ4jEWr37jceJe5afGb",null,0.0,0,-38.0,0,0,6740773,1,1.0,1.0,0.0,0.0,"2015-02-17T15:17:36.000+00:00",null]
